	//入队(添加一个数据）
	public void add(int value){
		/*if(size>=length){
			System.out.println("当前队列已满");
			return;
		}*/
		check();
		array[size] = value;
		size++;  //当前排队人数加加
	}

	//出队（删除一个数据）
	public int remove(){
		if(size<=0){
			System.out.println("当前队列没有数据");
			return 0;
		}
		int result = array[0];
		/*for(int i=0;i<size-1;i++){
			array[i] = array[i+1];
		}*/
		System.arraycopy(array, 1, array, 0, size-1);
		array[--size] = 0;
