```java
//聊天室的服务端
public class ChatServer {
	private ServerSocket ss;
	private Set<Socket> clients;

	public ChatServer(){
		try {
			ss = new ServerSocket(8888);
			clients = new HashSet<Socket>();
			System.out.println("聊天室的服务器端已经启动...");
			while(true){
				Socket socket = ss.accept();
				clients.add(socket);
				//启动一个线程单独处理该客户端
				new ChatThread(socket,clients).start();
			}
		} catch (Exception e) {
			e.printStackTrace();
		}finally{
			try {
				ss.close();
			} catch (IOException e) {
				e.printStackTrace();
			}
		}
	}
	public static void main(String[] args) {
		new ChatServer();
	}
}

//接收客户端的数据并转发给所有的客户端
class ChatThread extends Thread{
	private Socket currentSocket;  //当前客户端
	private Set<Socket> clients;
	public ChatThread(Socket currentSocket,Set<Socket> clients) {
		this.currentSocket = currentSocket;
		this.clients = clients;
	}

	@Override
	public void run(){
		BufferedReader br = null;
		try {
			//读数据
			br = new BufferedReader(new InputStreamReader(currentSocket.getInputStream()));
			String str = null;
			while((str=br.readLine())!=null){
				//转发给所有的客户端
				for(Socket client:clients){
					PrintWriter pw = new PrintWriter(client.getOutputStream());
					pw.println(str);
					pw.flush();
				}
			}
		} catch (Exception e) {
			e.printStackTrace();
		}
	}
}

