```java
public class UDPClient {
	public static void main(String[] args) throws Exception{
		//1.创建DatagramSocket对象，绑定IP地址和端口号
		DatagramSocket ds = new DatagramSocket();
		//2.发送数据包
		byte[] buf = new byte[2048];
		buf = "give me your time".getBytes();
		InetAddress address = InetAddress.getByName("127.0.0.1");  //根据主机名获取InetAddress对象
		DatagramPacket dp = new DatagramPacket(buf, buf.length,address,8899);
		ds.send(dp);
		//3.接收数据包
		DatagramPacket dp1 = new DatagramPacket(buf, buf.length);
		ds.receive(dp1);
		//输出数据包数据
		String message = new String(dp1.getData());
		System.out.println("接收服务器端数据:"+message);
		//关闭资源
		ds.close();
	}
}

